import './App.css';
import Header from './Components/Header'
import Todos from './Components/Todos';
import {Footer} from './Components/Footer';
import {Add} from './Components/Add';
import React, { useState } from 'react';


function App() {

  const addTodo = (title, description) => {
      console.log("I'm adding Todo", title, description)
      let sno = todoslist[todoslist.length - 1].sno + 1
      const myTodo = {
          sno: sno,
          title: title,
          desc: description
      }
  }

  const onDelete =(todo)=>{
    settodoslist(todoslist.filter((e)=>{
      return e!==todo;
    }))
  }
  const [todoslist, settodoslist] = useState([
    {
      sno: 1,
      title: "Let's Play",
      desc: "Play outdoor games"
    },
    {
      sno: 2,
      title: "Let's Eat",
      desc: "Go to Restaurant"
    }
  ]);
  return (
    <>
      <Header title="My ToDos List" searchBar={false}/>
      <Todos todos={todoslist} onDelete={onDelete}/>
      <Add addTodo={addTodo}/>
      <Footer/>
    </>
  );
}

export default App;
